char *strnew(const char *s)
{
    //                                    + 1 is the extra char for '\0'
    return strcpy((char*)malloc(strlen(s) + 1), s);
}

typedef struct {
    char *word;
    int count;
} Counter;

Counter *counters[MAX_WORDS];
int n_counters;

Counter *NewCounter(const char *s)
{
    Counter *x = (Counter *)malloc(sizeof(Counter));
    x->word = strnew(s);
    x->count = 1;
    return x;
}

