    //Counter **p;    // Array of pointers
    
    //for (p = counters; *p != NULL && strcmp((**p).word, s); p++) {}
    
    //return *p;
//}

void SortPointers(void **v, size_t n, int(*f)(const void *, const void *))
{
    void *m;
    for (size_t i = 1; i < n; i++)
        for (size_t j = n - 1; j >= i; j--)
            if (!f(v[j - 1], v[j]))
            {
                m = v[j - 1];
                v[j - 1] = v[j];
                v[j] = m;
            }
}

void SortCounters(int(*f)(const Counter *, const Counter *))
{
    SortPointers((void **) counters, n_counters,
                 (int(*)(const void *, const void *)) f);
}

int ltbyword(const Counter *x, const Counter *y)
{
    return strcmp(x->word, y->word) <= 0;
}

int ltbycount(const Counter *x, const Counter *y)
{
    return x->count >= y->count || x-> count == y->count && ltbyword(x, y);
}

void PrintCounter(const Counter *p)
{
    printf("%-24s %4d\n", p->word, p->count);
}

void IteratePointers(void **v, size_t n, void(*f)(void *))
{
    while (n--)
        f(*v++);
}

void PrintCounters(void)
{
    IteratePointers((void **) counters, n_counters, (void(*)(void *)) PrintCounter);
}

typedef enum {byword, bycount} SortCriterium;

/////////////////////////////////////// 04-lista-numero-linhas

typedef struct {
    const char *word;
    const int line;
} Pair;

Pair *pairs[MAX_WORDS];
int n_pairs;

void SortPairs(int(*f)(const Pair *, const Pair *))
{
    SortPointers((void **)pairs, n_pairs, (int(*)(const void *,const void *))f);
}

Pair *NewPair(const char *s, int n)
{
    Pair *x = (Pair *)malloc(sizeof(Pair));
    x->word = s;
    *(int *)&x->line = n;
    return x;
}

const char *ExistsWord(char *s)
{
    Pair **p;
    for (p = pairs; *p != NULL && strcmp((*p)->word, s); p++) {}
    
    return *p == NULL ? NULL : (*p)->word;
}

int main()
{
    int c, n, nl;
    char s[256];
    const char *p;  // Shouldn't be changed once created!
    Pair **ip;

    // Stdin will not flush input characters onto the input stream until the newline character '\n'.
    //     So getc(stdin) has nothing to read until you hit enter. If you want to read the console
    //     input without hitting enter, you can use getch() function in conio.h library available
    //     with Windows compilers. getch() reads a character from stdin without waiting for a newline.

    ip = pairs;
    nl = 0;
    while (ungetc(getchar(), stdin) != EOF)  // EOF is the same as -1
    {
        nl++;
        while (c = SkipChars(stdin, &n),
               c != '\n')   // The condition for the while, last one after ","
        {
            ReadWord(stdin, s);
            
            if ((p = ExistsWord(s)) == NULL)
                p = strnew(s);  // String words are allocated outside, and so the need for 'const'
            *ip++ = NewPair(p, nl);
        }
        getchar();
    }
    n_pairs = ip - pairs;
    
    printf("%ld %ld\n", n_words, n_unique);
    
    SortCriterium sortby = bycount;
    SortCounters(sortby == byword ? ltbyword : ltbycount);
    PrintCounters();
        
    return 0;
}

